SELECT employe.prenom, dept.nodept, dept.nom FROM employe JOIN dept ON dept.nodept = employe.nodep;
SELECT dept.nodept AS "Numéro du département", dept.nom AS "Nom du département", employe.nom AS "Nom de l'employé" FROM dept JOIN employe ON dept.nodept = employe.nodep ORDER BY nodept;
SELECT dept.nodept, dept.nom, employe.nom FROM employe JOIN dept ON dept.nodept = employe.nodep WHERE dept.nom = "Distribution";
SELECT a.nom AS "Nom du sup", a.salaire AS "Salaire du Sup", b.nom AS "Nom de l'employé", b.salaire AS "Salaire de l'employé" FROM employe AS a JOIN employe AS b ON b.nosup=a.noemp WHERE a.salaire < b.salaire;
SELECT nom, salaire, nodep FROM employe WHERE salaire > ANY (SELECT salaire FROM employe WHERE nodep = 31);
SELECT nom, salaire, nodep FROM employe WHERE salaire > ALL (SELECT salaire FROM employe WHERE nodep = 31);
SELECT nom, titre FROM employe WHERE nodep = 31 AND titre = ANY (SELECT titre from employe WHERE nodep = 32);
SELECT nom, titre FROM employe WHERE nodep = 31 AND titre NOT IN (SELECT titre from employe WHERE nodep = 32);
SELECT nom, titre, salaire FROM employe WHERE titre = (SELECT titre from employe WHERE nom = "fairent") AND salaire = (SELECT salaire FROM employe WHERE nom = "fairent");
SELECT dept.nodept, dept.nom, employe.nom FROM dept LEFT JOIN employe ON dept.nodept = employe.nodep; 
SELECT titre, count(noemp) FROM employe GROUP BY titre;
SELECT nodep AS "Numéro de département", avg(salaire) AS "Moyenne des salaires", sum(salaire) AS "Somme des salaires" FROM employe GROUP BY nodep;
SELECT nodep, count(noemp) FROM employe GROUP BY nodep HAVING count(noemp) > 2; 
SELECT substring(nom, 1, 1), count(substring(nom, 1, 1)) FROM employe GROUP BY substring(nom, 1, 1) HAVING count(substring(nom, 1, 1)) > 2;
SELECT MAX(salaire), MIN(salaire), MAX(salaire)-MIN(salaire) AS "Ecart de salaire" FROM employe; 
SELECT count(DISTINCT titre) FROM employe; 
SELECT titre, count(noemp) FROM employe GROUP BY titre;
SELECT dept.nom, count(employe.noemp) AS "Nombre d'employé par département" FROM dept LEFT JOIN employe ON dept.nodept = employe.nodep GROUP BY dept.nom;
SELECT titre, avg(salaire) FROM EMPLOYE GROUP BY titre HAVING avg(salaire) > (SELECT avg(salaire) FROM employe WHERE titre = "représentant");
SELECT count(salaire), count(tauxcom) FROM employe;